import Pagy from 'pagy';
import 'intersection-observer';

document.addEventListener('turbolinks:load', () => {
  const observer = new IntersectionObserver((entries) => {
    const entry = entries[0];
    if (entry.isIntersecting) {
      const pagination = document.querySelector('#pagination');
      const nextLink = pagination.querySelector('.next a');
      if (nextLink) {
        const url = nextLink.getAttribute('href');
        nextLink.removeAttribute('href');
        fetch(url, {
          headers: { 'X-Requested-With': 'XMLHttpRequest' }
        })
          .then(response => response.json())
          .then(data => {
            const posts = data.posts.map(post => `
              <%= render post %>
            `).join('');
            const postsContainer = document.querySelector('#posts');
            postsContainer.insertAdjacentHTML('beforeend', posts);
            const pagyElement = document.createElement('div');
            pagyElement.innerHTML = data.pagy;
            const newPagination = pagyElement.querySelector('.pagy-bootstrap-nav');
            const newNextLink = newPagination.querySelector('.next a');
            if (newNextLink) {
              newNextLink.setAttribute('href', url);
            }
            pagination.replaceWith(newPagination);
          })
          .catch(error => console.error(error));
      }
    }
  });

  observer.observe(document.querySelector('#pagination .next'));
});
